  private void reportButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_reportButtonActionPerformed
    if (comboBox.getSelectedItem().equals("Report All Items")) {
      ReportsUseCases.getInstance().produceItemReport("All");
    } else if (comboBox.getSelectedItem().equals("Report Books")) {
      ReportsUseCases.getInstance().produceItemReport("Books");
    } else if (comboBox.getSelectedItem().equals("Report DVDs")) {
      ReportsUseCases.getInstance().produceItemReport("DVDs");
    } else if (comboBox.getSelectedItem().equals("Report All Patrons")) {
      ReportsUseCases.getInstance().producePatronReport(false);
    } else {
      ReportsUseCases.getInstance().producePatronReport(true);
    }
  }//GEN-LAST:event_reportButtonActionPerformed

